sys_ui_action.script:
function getTeamPlayerCount(team) {
	var count = 0;
	var gr = new GlideRecord('x_159204_feudnow_players');
	gr.addQuery('game', current.sys_id);
	gr.addQuery('team', team);
	gr.query();

	while (gr.next()) {
		count++;
	}

	return count;
}

// get the number of players for each team and figure out which team to add player to
var team_one_player_count = getTeamPlayerCount('team_one');
var team_two_player_count = getTeamPlayerCount('team_two');
var team = 'team_one';
var avatar_number = team_one_player_count + team_two_player_count;

if (team_one_player_count > team_two_player_count) {
	team = 'team_two';
}	

var gr = new GlideRecord('x_159204_feudnow_players');
gr.addQuery('game', current.sys_id);
gr.addQuery('player', gs.getUserID());
gr.addQuery('active', true);
gr.query();

if (!gr.next()) {
	gr.initialize();

	gr.player = gs.getUserID();
	gr.game = current.sys_id;
	gr.team = team;
	gr.avatar = 'https://joeschmoe.io/api/v1/' + avatar_number;

	gr.insert();	
}

var url = '/sp?id=feudnow&game_id=' + current.sys_id;
action.setRedirectURL(url);	



